CREATE OR REPLACE FUNCTION obter_descricao_log_tasy (cd_log_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255) := '';

BEGIN

if (cd_log_p IS NOT NULL AND cd_log_p::text <> '') then
	select	substr(ds_tipo,1,255)
	into STRICT	ds_retorno_w
	from 	tipo_log_tasy
	where	cd_log = cd_log_p;
end if;

return	ds_retorno_w;

end;
