CREATE OR REPLACE FUNCTION obter_descricao_macro (nr_sequencia_p text, ie_tipo_ret_p text) RETURNS varchar AS $body$
DECLARE


ds_macro_w	MACRO_PRONTUARIO.ds_macro%type := 0;
nm_macro_w	MACRO_PRONTUARIO.nm_macro%type := 0;
retorno_w	varchar(255) := '';


BEGIN
	
select	ds_macro, a.nm_macro
into STRICT	ds_macro_w, nm_macro_w
from	MACRO_PRONTUARIO a
where	a.nr_sequencia = (coalesce(nr_sequencia_p, '0'))::numeric;

if (ie_tipo_ret_p = 'DS') then
    retorno_w := SUBSTR(ds_macro_w,1,255);
elsif (ie_tipo_ret_p = 'NM') then
    retorno_w := SUBSTR(nm_macro_w,1,255);
end if;

return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
