CREATE OR REPLACE FUNCTION obter_descricao_motivo_sub_mat (nr_seq_motivo_subs_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

	select  substr(ds_motivo,1,200)
	into STRICT ds_retorno_w
	from   MOTIVO_SUBS_MATERIAL
	where IE_SITUACAO = 'A'
	and nr_sequencia = nr_seq_motivo_subs_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
