SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_descricao_opme (nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ds_opme_w	varchar(3000);
ds_material_w	varchar(254);

c01 CURSOR FOR
	SELECT	substr(b.ds_material,1,254) ds_material
	from	material b,
		agenda_pac_opme a
	where	b.cd_material = a.cd_material
	and	a.nr_seq_agenda = nr_seq_agenda_p
	and	a.ie_origem_inf	= 'I'
	order by a.nr_seq_apres desc;


BEGIN

open c01;
loop
fetch c01 into
	ds_material_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	begin
	if (coalesce(ds_opme_w::text, '') = '') then
		ds_opme_w := ds_material_w;
	else
		ds_opme_w := ds_opme_w || ', ' ||ds_material_w;
	end if;
	exception
	when others then
		ds_opme_w	:= ds_opme_w || substr(ds_material_w,1,(3000-length(ds_opme_w)));
	end;
	end;
end loop;
close c01;

return 	substr(ds_opme_w,1,3000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
