CREATE OR REPLACE FUNCTION obter_descricao_parametro ( cd_funcao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_parametro_w	varchar(500);


BEGIN
	begin

	select	substr(obter_desc_expressao(cd_exp_parametro, ds_parametro),1,500)
	into STRICT	ds_parametro_w
	from	funcao_parametro
	where	cd_funcao	= cd_funcao_p
	and 	nr_sequencia	= nr_sequencia_p;
	exception
		when others then
			ds_parametro_w	:= null;
	end;

return	ds_parametro_w;

end	;
$body$
LANGUAGE PLPGSQL
