CREATE OR REPLACE FUNCTION obter_descricao_pbs ( cd_pbs_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

select max(p.ds_pbs)
into STRICT ds_retorno_w
from pbs_record p
inner join material_pbs m
on p.cd_pbs = m.cd_pbs
and p.cd_pbs = cd_pbs_p
group by p.ds_pbs;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
