CREATE OR REPLACE FUNCTION obter_descricao_proced_cih (cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


ds_procedimento_w		PROCEDIMENTO.DS_PROCEDIMENTO%type;


BEGIN

BEGIN

SELECT ds_procedimento
INTO STRICT ds_procedimento_w
FROM procedimento
WHERE cd_procedimento = cd_procedimento_p
  AND ie_origem_proced = coalesce(ie_origem_proced_p,2);
EXCEPTION
	WHEN OTHERS THEN
		ds_procedimento_w := null;
END;

if (coalesce(ds_procedimento_w::text, '') = '') then

	begin

	SELECT ds_procedimento
	INTO STRICT ds_procedimento_w
	FROM procedimento
	WHERE cd_procedimento = cd_procedimento_p
	  AND ie_origem_proced = coalesce(ie_origem_proced_p,ie_origem_proced)
	  AND (ie_origem_proced = 2 or row_number() OVER () = 1);
	EXCEPTION
		WHEN OTHERS THEN
			ds_procedimento_w := Wheb_mensagem_pck.get_texto(308893); --'NÃ£o encontrado';
	END;

end if;

RETURN ds_procedimento_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
