CREATE OR REPLACE FUNCTION obter_descricao_processo (cd_processo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_processo_w	varchar(50);

BEGIN
ds_processo_w := '';

if (coalesce(cd_processo_p,0) > 0) then

	select	max(nm_processo)
	into STRICT	ds_processo_w
	from	processo
	where	cd_processo = cd_processo_p;

end if;

return	ds_processo_w;

end;
$body$
LANGUAGE PLPGSQL
