CREATE OR REPLACE FUNCTION obter_descricao_qtd_dor ( qt_dor_p bigint) RETURNS varchar AS $body$
DECLARE


vl_retorno_w	varchar(50);

BEGIN
vl_retorno_w := substr(wheb_mensagem_pck.get_texto(293929),1,50); /*sem dor*/
if (qt_dor_p IS NOT NULL AND qt_dor_p::text <> '') then
	begin
	if (qt_dor_p >= 1)	and (qt_dor_p <= 3)	then
		begin
		vl_retorno_w := substr(wheb_mensagem_pck.get_texto(293928),1,50); /*leve*/
		end;
	elsif (qt_dor_p >= 4)	and (qt_dor_p <= 7)	then
		begin
		vl_retorno_w := substr(wheb_mensagem_pck.get_texto(293930),1,50); /*moderado*/
		end;
	elsif (qt_dor_p >= 8)	then
		begin
		vl_retorno_w := substr(wheb_mensagem_pck.get_texto(293931),1,50); /*grave*/
		end;
	end if;
	end;
end if;

return vl_retorno_w;
end;
$body$
