CREATE OR REPLACE FUNCTION obter_descricao_software (nr_seq_capt_software_p bigint,ds_software_cap_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(512);


BEGIN

if (nr_seq_capt_software_p IS NOT NULL AND nr_seq_capt_software_p::text <> '') then
	select	max(nm_software)
	into STRICT	ds_retorno_w
	from	software
	where	nr_id_soft_cap = nr_seq_capt_software_p;
end if;

return coalesce(ds_retorno_w,ds_software_cap_p);

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
