CREATE OR REPLACE FUNCTION obter_descricao_tipo_protocolo (CD_TIPO_PROTOCOLO_P bigint, SOMENTE_ATIVO_P text DEFAULT 'S') RETURNS varchar AS $body$
DECLARE

DS_RETORNO_W varchar(255);

BEGIN
    SELECT	COALESCE(DS_TIPO_PROTOCOLO,'')
      INTO STRICT	DS_RETORNO_W
      FROM	TIPO_PROTOCOLO
     WHERE (coalesce(IE_SITUACAO,'A') = 'A' AND CD_TIPO_PROTOCOLO = CD_TIPO_PROTOCOLO_P ) OR (SOMENTE_ATIVO_P = 'N' AND CD_TIPO_PROTOCOLO = CD_TIPO_PROTOCOLO_P )
     ORDER BY 1;
RETURN DS_RETORNO_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
