CREATE OR REPLACE FUNCTION obter_descricao_toxina ( nr_sequencia_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	max(ds_toxina)
	into STRICT	ds_retorno_w
	from	TOXINA_BOTULINICA
	where	ie_situacao = 'A'
	and 	nr_sequencia	= nr_sequencia_p;

end if;

RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
