CREATE OR REPLACE FUNCTION obter_descr_amostra_lab ( nr_prescricao_p bigint, cd_exame_integracao_p text, nr_amostra_p bigint, ie_opcao_p bigint ) RETURNS varchar AS $body$
DECLARE


nm_exame_w	varchar(20);


BEGIN

begin

select 	substr(nm_exame,1,20)
into STRICT	nm_exame_w
from 	exame_laboratorio
where 	cd_exame_integracao = cd_exame_integracao_p || nr_amostra_p;

exception
when others then
	nm_exame_w := null;
end;

return nm_exame_w;

end;
