CREATE OR REPLACE FUNCTION obter_descr_func_idioma_junit (tipo_idioma_p text, ds_funcao_idioma_p text) RETURNS varchar AS $body$
DECLARE

ds_funcao_w varchar(255) := '';
nr_seq_idioma_w	bigint	:= 1;


BEGIN
if (tipo_idioma_p IS NOT NULL AND tipo_idioma_p::text <> '') and (ds_funcao_idioma_p IS NOT NULL AND ds_funcao_idioma_p::text <> '') then

	if (tipo_idioma_p	= 'PT') then
		nr_seq_idioma_w	:= 1;
	elsif (tipo_idioma_p = 'ES') then
		nr_seq_idioma_w	:= 2;
	elsif (tipo_idioma_p = 'EN') then
		nr_seq_idioma_w	:= 5;
	end if;


        select obter_desc_expressao_idioma(f.cd_exp_funcao,null,nr_seq_idioma_w)
	into STRICT	ds_funcao_w
	from funcao f
	where f.ds_funcao = ds_funcao_idioma_p
	and f.ie_situacao in ('A', 'C');
end if;

return ds_funcao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
