CREATE OR REPLACE FUNCTION obter_descr_gerenciador (nr_seq_gerenciador_p bigint) RETURNS varchar AS $body$
DECLARE


ds_gerenciador_w varchar(255);


BEGIN

if (nr_seq_gerenciador_p IS NOT NULL AND nr_seq_gerenciador_p::text <> '') then

	select max(ds_gerenciador)
	into STRICT ds_gerenciador_w
	from lote_ent_gerenc
	where nr_sequencia = nr_seq_gerenciador_p;

end if;

return ds_gerenciador_w;

end;
