CREATE OR REPLACE FUNCTION obter_descr_mot_bloq_ag ( cd_agenda_p bigint, dt_inicial_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE


nr_seq_agenda_bloq_w	bigint;
ds_retorno_w		varchar(255);


C01 CURSOR FOR
	SELECT 	a.nr_sequencia
	from 	agenda_bloqueio a
	where a.cd_agenda = cd_agenda_p
	and ((a.dt_inicial  between dt_inicial_p and dt_final_p) or (a.dt_final between dt_inicial_p and dt_final_p) or (dt_inicial_p between a.dt_inicial and a.dt_final) or (dt_final_p between a.dt_inicial and a.dt_final))
	order by dt_inicial,
		 dt_final;

BEGIN

open C01;
loop
fetch C01 into
	nr_seq_agenda_bloq_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	nr_seq_agenda_bloq_w := nr_seq_agenda_bloq_w;
	end;
end loop;
close C01;

if (nr_seq_agenda_bloq_w IS NOT NULL AND nr_seq_agenda_bloq_w::text <> '') then
	select	max(substr(obter_valor_dominio(1007,ie_motivo_bloqueio),1,100))
	into STRICT	ds_retorno_w
	from 	agenda_bloqueio
	where	nr_sequencia = nr_seq_agenda_bloq_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
