SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_agenda_bloqueio (cd_agenda_p bigint, dt_horario_p timestamp, dt_date_p timestamp) RETURNS varchar AS $body$
DECLARE


nr_periodo_w			bigint	:= 0;
nr_horario_w			bigint	:= 0;
nr_dia_w				bigint	:= 0;
nr_dia_hor_w			bigint	:= 0;
nr_seq_bloqueio_w		bigint	:= 0;
ie_bloqueio_w			varchar(1);
ie_motivo_bloqueio_w	varchar(255);
ie_dia_semana_p		    integer;


BEGIN

select obter_cod_dia_semana(dt_date_p)
into STRICT ie_dia_semana_p
;

if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (dt_horario_p IS NOT NULL AND dt_horario_p::text <> '') and (ie_dia_semana_p IS NOT NULL AND ie_dia_semana_p::text <> '') then
	/* consistir bloqueio período */

	begin
	select	max(nr_sequencia)
	into STRICT	nr_periodo_w
	from	agenda_bloqueio
	where	cd_agenda = cd_agenda_p
	and	trunc(dt_horario_p) between dt_inicial and dt_final
	and	coalesce(hr_inicio_bloqueio::text, '') = ''
	and	coalesce(hr_final_bloqueio::text, '') = ''
	and	coalesce(ie_dia_semana::text, '') = '';
	exception
		when others then
		nr_periodo_w := 0;
	end;

	/* consistir bloqueio horário */

	begin
	select	max(nr_sequencia)
	into STRICT	nr_horario_w
	from	agenda_bloqueio
	where	cd_agenda = cd_agenda_p
	and	trunc(dt_horario_p) between dt_inicial and dt_final
	and	dt_horario_p between	to_date(to_char(dt_horario_p,'dd/mm/yyyy') ||' '|| to_char(hr_inicio_bloqueio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') and 						to_date(to_char(dt_horario_p,'dd/mm/yyyy') ||' '|| to_char(hr_final_bloqueio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
	and	(hr_inicio_bloqueio IS NOT NULL AND hr_inicio_bloqueio::text <> '')
	and	(hr_final_bloqueio IS NOT NULL AND hr_final_bloqueio::text <> '')
	and	hr_inicio_bloqueio < hr_final_bloqueio
	and	coalesce(ie_dia_semana::text, '') = '';
	exception
		when others then
		nr_horario_w := 0;
	end;

	/* consistir bloqueio dia */

	begin
	select 	max(nr_sequencia)
	into STRICT	nr_dia_w
	from	agenda_bloqueio
	where	cd_agenda = cd_agenda_p
	and	trunc(dt_horario_p) between dt_inicial and dt_final
	and	coalesce(hr_inicio_bloqueio::text, '') = ''
	and	coalesce(hr_final_bloqueio::text, '') = ''
	and	(ie_dia_semana IS NOT NULL AND ie_dia_semana::text <> '')
	and	((ie_dia_semana = ie_dia_semana_p) or (ie_dia_semana = 9));
	exception
		when others then
		nr_dia_w := 0;
	end;

	/* consistir bloqueio dia e horário */

	begin
	select	max(nr_sequencia)
	into STRICT	nr_dia_hor_w
	from	agenda_bloqueio
	where	cd_agenda = cd_agenda_p
	and	trunc(dt_horario_p) between dt_inicial and dt_final
	and	dt_horario_p between	to_date(to_char(dt_horario_p,'dd/mm/yyyy') ||' '|| to_char(hr_inicio_bloqueio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') and 						to_date(to_char(dt_horario_p,'dd/mm/yyyy') ||' '|| to_char(hr_final_bloqueio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
	and	(hr_inicio_bloqueio IS NOT NULL AND hr_inicio_bloqueio::text <> '')
	and	(hr_final_bloqueio IS NOT NULL AND hr_final_bloqueio::text <> '')
	and	hr_inicio_bloqueio < hr_final_bloqueio
	and	(ie_dia_semana IS NOT NULL AND ie_dia_semana::text <> '')
	and	((ie_dia_semana = ie_dia_semana_p) or (ie_dia_semana = 9));
	exception
		when others then
		nr_dia_hor_w := 0;
	end;

	if (nr_dia_hor_w > 0) then
		nr_seq_bloqueio_w	:= nr_dia_hor_w;
	elsif (nr_dia_w > 0) then
		nr_seq_bloqueio_w	:= nr_dia_w;
	elsif (nr_horario_w > 0) then
		nr_seq_bloqueio_w	:= nr_horario_w;
	elsif (nr_periodo_w > 0) then
		nr_seq_bloqueio_w	:= nr_periodo_w;
	else
		nr_seq_bloqueio_w	:= 0;
	end if;
end if;

begin
Select	' ' || obter_valor_dominio(1007,ie_motivo_bloqueio)
into STRICT	ie_motivo_bloqueio_w
from	agenda_bloqueio
where	nr_sequencia = nr_seq_bloqueio_w;
exception
when others then
	ie_motivo_bloqueio_w	:= '';
end;

return ie_motivo_bloqueio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
