SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_agenda_hoje (cd_pessoa_fisica_p bigint) RETURNS varchar AS $body$
DECLARE

 
hr_agenda_w		varchar(50);
dt_tipo_w			varchar(255);
ds_resultado_w		varchar(4000) := '';
ds_tipo_w		varchar(255);
nr_seq_agenda_w 		agenda_consulta.nr_sequencia%type;

 
/* verificar agenda serviço */
 
c01 CURSOR FOR 
SELECT	b.nr_sequencia 
from	agenda a, 
	agenda_consulta b 
where	a.cd_agenda = b.cd_agenda 
and	a.cd_tipo_agenda = 5 
and	b.cd_pessoa_fisica = cd_pessoa_fisica_p 
and	b.ie_status_agenda not in ('C','L','B') 
and	b.dt_agenda between inicio_dia(clock_timestamp()) and fim_dia(clock_timestamp()) 
order by b.dt_agenda desc;

	 
/* verificar agenda consulta */
 
c02 CURSOR FOR 
SELECT	b.nr_sequencia 
from	agenda a, 
	agenda_consulta b 
where	a.cd_agenda = b.cd_agenda 
and	a.cd_tipo_agenda in (3,4) 
and	b.cd_pessoa_fisica = cd_pessoa_fisica_p 
and	b.ie_status_agenda not in ('C','L','B') 
and	b.dt_agenda between inicio_dia(clock_timestamp()) and fim_dia(clock_timestamp()) 
order by b.dt_agenda desc;

 
/* verificar agenda exames */
 
c03 CURSOR FOR 
SELECT	b.nr_sequencia 
from	agenda a, 
	agenda_paciente b 
where	a.cd_agenda = b.cd_agenda 
and	a.cd_tipo_agenda = 2 
and	b.cd_pessoa_fisica = cd_pessoa_fisica_p 
and	b.ie_status_agenda not in ('C','L','B') 
and	b.dt_agenda between inicio_dia(clock_timestamp()) and fim_dia(clock_timestamp()) 
order by b.dt_agenda desc;

 

BEGIN 
 
 
 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
 
	/* verificar agenda serviço */
 
	open c01;
	loop 
	fetch c01 into 
		nr_seq_agenda_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin 
		 
		if (nr_seq_agenda_w > 0) then 
			select	to_char(b.dt_agenda,'hh24:mi') ds_hora, 
				substr(obter_desc_expressao(298345),1,30) ds_tipo --Serviço. 
			into STRICT	hr_agenda_w, 
				ds_tipo_w 
			from	agenda_consulta b 
			where	b.nr_sequencia = nr_seq_agenda_w;
 
			ds_resultado_w := ds_resultado_w || hr_agenda_w || ' ' || '(' || ds_tipo_w || ')' || ' / ';
									 
		end if;	
		end;
	end loop;
	close c01;
	 
 
		 
	/* verificar agenda consulta */
 
	open c02;
	loop 
	fetch c02 into 
		nr_seq_agenda_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin 
		if (nr_seq_agenda_w > 0) then 
			select	to_char(b.dt_agenda,'hh24:mi') ds_hora, 
				substr(obter_desc_expressao(285916),1,30) ds_tipo -- Consulta. 
			into STRICT	hr_agenda_w, 
				ds_tipo_w 
			from	agenda_consulta b 
			where	b.nr_sequencia = nr_seq_agenda_w;
 
			ds_resultado_w := ds_resultado_w || hr_agenda_w || ' ' ||'(' || ds_tipo_w || ')' || ' / ';
 
		end if;	
		end;
	end loop;
	close c02;
	 
	 
			 
	/* verificar agenda exames e cirurgia */
 
	open c03;
	loop 
	fetch c03 into 
		nr_seq_agenda_w;
	EXIT WHEN NOT FOUND; /* apply on c03 */
		begin 
		if (nr_seq_agenda_w > 0) then 
			select	to_char(b.dt_agenda,'hh24:mi') ds_hora, 
				substr(obter_desc_expressao(761511),1,330) ds_tipo -- Exame 
			into STRICT	hr_agenda_w, 
				ds_tipo_w 
			from	agenda_paciente b 
			where	b.nr_sequencia = nr_seq_agenda_w;
 
			ds_resultado_w := ds_resultado_w || hr_agenda_w || ' ' ||'(' || ds_tipo_w || ')' || ' / ';
 
		end if;	
		end;
	end loop;
	close c03;	
	 
	ds_resultado_w := substr(ds_resultado_w,1, length(ds_resultado_w) -3);
	ds_resultado_w := substr(ds_resultado_w,1,4000);
end if;
 
 
 
return ds_resultado_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_desc_agenda_hoje (cd_pessoa_fisica_p bigint) FROM PUBLIC;

