CREATE OR REPLACE FUNCTION obter_desc_agrup_censo ( nr_seq_agrupamento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(50);


BEGIN

if (coalesce(nr_seq_agrupamento_p,0)	= 0) then
	ds_retorno_w	:= Wheb_mensagem_pck.get_texto(308917); --'Sem agrupamento';
else
	ds_retorno_w	:= substr(Obter_desc_agrup_setor(nr_seq_agrupamento_p),1,50);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
