CREATE OR REPLACE FUNCTION obter_desc_amostra_selecionada ( ds_materiais_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(4000);

BEGIN
ds_retorno_w := obter_select_concatenado_bv( ' select ds_amostra      
					       from   tipo_amostra_patologia  
					       where  nr_sequencia in (' || ds_materiais_p || ') 
					       order by 1 ','',', ');					
return	ds_retorno_w;

end;
$body$
