CREATE OR REPLACE FUNCTION obter_desc_approver (NR_SEQ_P bigint) RETURNS varchar AS $body$
DECLARE

ds_approver varchar(100);


BEGIN
    select 
           substr(COALESCE(obter_nome_pf(a.cd_pessoa_fisica), (select x.ds_funcao ds from funcao_pessoa_fisica x where x.nr_sequencia = a.nr_seq_funcao_pf), CASE WHEN a.ie_equipe_cirurgica='S' THEN  'Surgical Team' WHEN a.ie_equipe_cirurgica='N' THEN  NULL END ), 1, 100) into STRICT ds_approver 
    from 
        mult_aprov_prof a 
    where 
        nr_sequencia = NR_SEQ_P;
    return ds_approver;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
