CREATE OR REPLACE FUNCTION obter_desc_apres_brasindice ( cd_apresentacao_p text) RETURNS varchar AS $body$
DECLARE


ds_apresentacao_w		varchar(155) := '';


BEGIN

if (cd_apresentacao_p IS NOT NULL AND cd_apresentacao_p::text <> '') then
	select	ds_apresentacao
	into STRICT	ds_apresentacao_w
	from	brasindice_apresentacao
	where	cd_apresentacao = cd_apresentacao_p
	and	coalesce(ie_situacao, 'A') = 'A';

	return ds_apresentacao_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
