CREATE OR REPLACE FUNCTION obter_desc_area_procedimento ( cd_area_procedimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_area_procedimento_w	varchar(40);


BEGIN
if (cd_area_procedimento_p IS NOT NULL AND cd_area_procedimento_p::text <> '') then

	select	max(ds_area_procedimento)
	into STRICT	ds_area_procedimento_w
	from	area_procedimento
	where	cd_area_procedimento = cd_area_procedimento_p;

end if;

return ds_area_procedimento_w;

end;
