CREATE OR REPLACE FUNCTION obter_desc_atributo_idioma ( nm_tabela_p text, nm_atributo_p text, nr_seq_idioma_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(4000);

BEGIN

if (nr_seq_idioma_p IS NOT NULL AND nr_seq_idioma_p::text <> '') and (nr_seq_idioma_p > 0) then
	select	max(ds_traducao)
	into STRICT	ds_retorno_w
	from	tab_atributo_desc_idioma
	where	nm_tabela = nm_tabela_p
	and	nm_atributo = nm_atributo_p
	and	nr_seq_idioma = nr_seq_idioma_p;

	if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') and (ds_retorno_w <> ' ') then
		return ds_retorno_w;
	end if;
end if;

select	ds_atributo
into STRICT	ds_retorno_w
from	tabela_atributo
where	nm_tabela = nm_tabela_p
and	nm_atributo = nm_atributo_p;

return	ds_retorno_w;

end;
$body$
