SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_atributo_sepse (nr_seq_atributo_w bigint, nr_sequencia_w bigint default null) RETURNS varchar AS $body$
DECLARE


qt_idade_w			integer;
qt_idade_dia_w		bigint;
ds_retorno_w		varchar(255);
nr_seq_escala_p		bigint;
nr_atendimento_p	bigint;


BEGIN

select 	max(nr_seq_escala)
into STRICT	nr_seq_escala_p
from 	escala_sepse_infantil_item
where 	nr_seq_atributo = nr_seq_atributo_w
and		nr_sequencia = nr_sequencia_w;

if (nr_seq_escala_p IS NOT NULL AND nr_seq_escala_p::text <> '') then
	select 	nr_atendimento
	into STRICT 	nr_atendimento_p
	from 	escala_sepse_infantil
	where	nr_sequencia = nr_seq_escala_p;

	select	max((obter_idade_pf(obter_pessoa_atendimento(nr_atendimento_p,'C'),clock_timestamp(),'A'))::numeric ),
			trunc(max((obter_idade_pf(obter_pessoa_atendimento(nr_atendimento_p,'C'),clock_timestamp(),'DIA'))::numeric ))
	into STRICT	qt_idade_w,
			qt_idade_dia_w
	;

	select	max(nm_atributo)
	into STRICT	ds_retorno_w
	from	sepse_atributo_cliente a,
			sepse_atrib_cliente_regra b
	where	a.nr_seq_atributo = nr_seq_atributo_w
	and   	b.nr_seq_atrib_cliente = a.nr_sequencia
	and 	((qt_idade_dia_w between b.qt_idade_min_dias and b.qt_idade_max_dias)
	or (qt_idade_w between b.qt_idade_min and b.qt_idade_max));
	
else
	select	max(nm_atributo)
	into STRICT	ds_retorno_w
	from	sepse_atributo_cliente
	where	nr_seq_atributo = nr_seq_atributo_w;
end if;

if (coalesce(trim(both ds_retorno_w)::text, '') = '') then
	
	select	max(coalesce(obter_desc_expressao(CD_EXP_DESC_ITEM), nm_atributo))
	into STRICT	ds_retorno_w
	from	sepse_atributo
	where	nr_sequencia = nr_seq_atributo_w;
	
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
