CREATE OR REPLACE FUNCTION obter_desc_atributo_visao ( nm_atributo_p text, nr_seq_visao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_label_w	varchar(255) := null;


BEGIN

if (nm_atributo_p IS NOT NULL AND nm_atributo_p::text <> '') and (coalesce(nr_seq_visao_p,0) > 0) then
	select	max(coalesce(substr(obter_desc_expressao(cd_exp_desc),1,254),substr(obter_desc_expressao(cd_exp_label),1,254)))
	into STRICT		ds_label_w
	from		tabela_atributo a,
				tabela_visao b
	where		a.nm_tabela		= b.nm_tabela
	and		a.nm_atributo 	= nm_atributo_p
	and		b.nr_sequencia = nr_seq_visao_p;
end if;

return	ds_label_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
