CREATE OR REPLACE FUNCTION obter_desc_atrib_log_gv (ds_atributo_p text, ds_valor_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(255);


BEGIN 
 
	if (ds_atributo_p = 'IE_STATUS') then 
		ds_retorno_w := obter_valor_dominio(1408,ds_valor_p);
	elsif (ds_atributo_p = 'IE_SOLICITACAO') then 
		ds_retorno_w := obter_valor_dominio(1407,ds_valor_p);
	elsif (ds_atributo_p = 'IE_CLINICA') then 
		ds_retorno_w := obter_valor_dominio(17,ds_valor_p);
	elsif (ds_atributo_p = 'IE_TIPO_VAGA') then 
		ds_retorno_w := obter_valor_dominio(1410,ds_valor_p);
	elsif (ds_atributo_p = 'CD_SETOR_DESEJADO') then 
		select 	ds_setor_atendimento 
		into STRICT 	ds_retorno_w 
		from 	setor_atendimento 
		where 	cd_setor_atendimento = ds_valor_p;
	elsif (ds_atributo_p = 'CD_CONVENIO') then 
		select 	ds_convenio 
		into STRICT 	ds_retorno_w 
		from 	convenio 
		where 	cd_convenio = ds_valor_p;
	elsif (ds_atributo_p = 'CD_PESSOA_FISICA') or (ds_atributo_p = 'CD_MEDICO') then 
		select	SUBSTR(OBTER_NOME_PF(CD_PESSOA_FISICA),0,255) 
		into STRICT	ds_retorno_w 
		from	pessoa_fisica 
		where	cd_pessoa_fisica = ds_valor_p;
	else 
		ds_retorno_w := ds_valor_p;
	end if;	
 
return	ds_retorno_w;
 
end;
