CREATE OR REPLACE FUNCTION obter_desc_bandeira_cartao ( nr_seq_bandeira_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255)	:= null;


BEGIN

if (nr_seq_bandeira_p IS NOT NULL AND nr_seq_bandeira_p::text <> '') then
	select	coalesce(max(a.ds_bandeira),'')
	into STRICT	ds_retorno_w
	from	bandeira_cartao_cr a
	where	a.nr_sequencia = nr_seq_bandeira_p;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
