CREATE OR REPLACE FUNCTION obter_desc_bsc_indicador ( nr_seq_indicador_p bigint) RETURNS varchar AS $body$
DECLARE


nm_indicador_w			varchar(255);


BEGIN

if (nr_seq_indicador_p IS NOT NULL AND nr_seq_indicador_p::text <> '') then

	 select	substr(coalesce(max(obter_desc_expressao(CD_EXP_NM_INDICADOR,nm_indicador)),''),1,255)
	 into STRICT	nm_indicador_w
	 from	bsc_indicador
	 where	nr_sequencia = nr_seq_indicador_p;

end if;

return nm_indicador_w;

end;
$body$
LANGUAGE PLPGSQL
