CREATE OR REPLACE FUNCTION obter_desc_bs_sol_dialise (nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_componente_bolsa_w	smallint;
ds_material_w			varchar(255);


BEGIN

	select coalesce(max(1),0)
	into STRICT ie_componente_bolsa_w
	from prescr_material a,
		 hd_prescricao b,
		 prescr_solucao c
	where a.nr_prescricao = nr_prescricao_p
	and a.nr_prescricao = b.nr_prescricao
	and a.nr_prescricao = c.nr_prescricao
	and a.nr_sequencia_solucao = nr_seq_solucao_p
	and c.nr_seq_solucao = 1
	and a.cd_unidade_medida_dose = 'Bs';

	if (ie_componente_bolsa_w = 1) then
		begin
			select coalesce(max(substr(obter_desc_material(a.cd_material) || ' ' || a.qt_dose || a.cd_unidade_medida_dose,1,255)),'')
			into STRICT ds_material_w
			from prescr_material a,
				 hd_prescricao b,
				 prescr_solucao c
			where a.nr_prescricao = nr_prescricao_p
			and a.nr_prescricao = b.nr_prescricao
			and a.nr_prescricao = c.nr_prescricao
			and a.nr_sequencia_solucao = nr_seq_solucao_p
			and c.nr_seq_solucao = 1
			and a.cd_unidade_medida_dose = 'Bs';
		end;
	else
		begin
			select coalesce(max(substr(obter_desc_material(a.cd_material) || ' ' || a.qt_dose || a.cd_unidade_medida_dose,1,255)),'')
			into STRICT ds_material_w
			from prescr_material a,
				 hd_prescricao b,
				 prescr_solucao c
			where a.nr_prescricao = nr_prescricao_p
			and a.nr_prescricao = b.nr_prescricao
			and a.nr_prescricao = c.nr_prescricao
			and a.nr_sequencia_solucao = nr_seq_solucao_p
			and c.nr_seq_solucao = 1;
		end;
	end if;

return	ds_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
