CREATE OR REPLACE FUNCTION obter_desc_cabine_manip (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE



ds_retorno_w varchar(80);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select  max(c.ds_cabine)
	into STRICT    ds_retorno_w
	from    can_ordem_prod a,
			far_etapa_producao b,
		far_cabine_seg_biol c
	where   a.nr_seq_etapa_prod = b.nr_sequencia
	and 	b.nr_seq_cabine = c.nr_sequencia
	and     a.nr_sequencia = nr_sequencia_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
