CREATE OR REPLACE FUNCTION obter_desc_calc_terap (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_dose_terapeutica_w	varchar(100);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
		select	substr(obter_desc_expressao(cd_exp_dose,ds_dose_terapeutica),1,100)
		into STRICT	ds_dose_terapeutica_w
		from	regra_dose_terap
		where	nr_sequencia	= nr_sequencia_p;

		if (position('/' in ds_dose_terapeutica_w) > 0) then
			ds_dose_terapeutica_w := substr(ds_dose_terapeutica_w,0,position('/' in ds_dose_terapeutica_w)-1);
		end if;

	end;
end if;

return ds_dose_terapeutica_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
