SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_campo_srpa_nulo (nr_seq_avaliacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000) := '';
ds_item_w	varchar(255) := '';

c01 CURSOR FOR
SELECT obter_desc_item_srpa(nr_seq_item) ds_item
from srpa_aval_item
where nr_seq_avaliacao = nr_seq_avaliacao_p
and coalesce(nr_seq_result::text, '') = '';


BEGIN
	open c01;
	loop
	fetch c01 into
		ds_item_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		ds_retorno_w   := ds_retorno_w || ' ' || ds_item_w || ', ';
	end loop;
	close c01;

return	trim(both substr(ds_retorno_w, 0, length(ds_retorno_w)-2));

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
