CREATE OR REPLACE FUNCTION obter_desc_cargo (CD_CARGO_P bigint) RETURNS varchar AS $body$
DECLARE


DS_CARGO_W		varchar(100);


BEGIN

IF (CD_CARGO_P IS NOT NULL AND CD_CARGO_P::text <> '') THEN
	BEGIN

	SELECT	substr(obter_desc_expressao(cd_exp_cargo,ds_cargo),1,80)
	INTO STRICT	DS_CARGO_W
	FROM	CARGO
	WHERE 	CD_CARGO	= CD_CARGO_P;

