CREATE OR REPLACE FUNCTION obter_desc_categoria_cid (CD_CATEGORIA_P text) RETURNS varchar AS $body$
DECLARE


DS_CATEGORIA_W		varchar(240);


BEGIN

IF (CD_CATEGORIA_P IS NOT NULL AND CD_CATEGORIA_P::text <> '') THEN
	BEGIN

	SELECT	substr(DS_CATEGORIA_CID,1,240)
	INTO STRICT	DS_CATEGORIA_W
	FROM	CID_CATEGORIA
	WHERE	CD_CATEGORIA_CID	= CD_CATEGORIA_P;

	END;
END IF;

RETURN DS_CATEGORIA_W;

END;
