CREATE OR REPLACE FUNCTION obter_desc_cat_conv_conta_pac (cd_categoria_p text, cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (cd_categoria_p IS NOT NULL AND cd_categoria_p::text <> '') and (cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '') then

	select	substr(max(a.ds_categoria),1,254)
	into STRICT	ds_retorno_w
	from	categoria_convenio a
	where	a.cd_categoria	= cd_categoria_p
	and	a.cd_convenio   = cd_convenio_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
