CREATE OR REPLACE FUNCTION obter_desc_causa_morte (cd_paciente_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(255);
			

BEGIN 
 
SELECT	max(a.ds_causa) 
INTO STRICT	ds_retorno_w 
FROM	atendimento_paciente x, 
	causa_morte a, 
	declaracao_obito b 
WHERE	a.nr_sequencia = b.nr_seq_causa_morte 
AND	b.nr_atendimento = x.nr_atendimento 
and	x.cd_pessoa_fisica = cd_paciente_p 
AND	(dt_obito IS NOT NULL AND dt_obito::text <> '') 
and	(a.ds_causa IS NOT NULL AND a.ds_causa::text <> '');
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
