SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_cep_loc (cd_cep_p text) RETURNS varchar AS $body$
DECLARE


ds_resultado_w		varchar(240)	:= '';
ie_cep_novo_w		varchar(01)	:= '';
ie_cep_logradouro_w	varchar(01)	:= 'N';

c01 CURSOR FOR
	SELECT	a.nm_localidade
	from	cep_loc a
	where	a.cd_cep	= cd_cep_p
	  and	coalesce(a.nr_seq_superior, 0) = 0
	  and	ie_cep_novo_w	= 'S'
	
union

	SELECT	b.nm_localidade
	from	cep_localidade b
	where	b.cd_localidade	= cd_cep_p
	and	ie_cep_novo_w	= 'N';


BEGIN

begin

begin
if (cd_cep_p IS NOT NULL AND cd_cep_p::text <> '') then
	begin

	select	coalesce(vl_parametro, vl_parametro_padrao)
	into STRICT	ie_cep_novo_w
	from	funcao_parametro
	where	cd_funcao	= 0
	and	nr_sequencia	= 25;

	open c01;
	loop
	EXIT WHEN NOT FOUND; /* apply on c01 */
		fetch c01 into ds_resultado_w;
	end loop;
	close c01;
	end;
end if;
exception
	when others then
	ds_resultado_w	:= '';
end;

if	((cd_cep_p IS NOT NULL AND cd_cep_p::text <> '') and ((coalesce(ds_resultado_w::text, '') = '') or (ds_resultado_w = ''))) then
	begin

	if (ie_cep_novo_w		= 'S') then
		select	max(b.nm_localidade)
		into STRICT	ds_resultado_w
		from	cep_loc b,
			cep_log a
		where	(a.nr_seq_loc)::numeric 	= b.nr_sequencia
		and	a.cd_cep	= cd_cep_p;
		if (coalesce(ds_resultado_w::text, '') = '') then
			select	max(b.nm_localidade)
			into STRICT	ds_resultado_w
			from	cep_loc b
			where	b.cd_cep	= cd_cep_p;
		end if;
	else
		select	max(nm_localidade)
		into STRICT	ds_resultado_w
		from	cep_logradouro
		where	cd_logradouro	= cd_cep_p;
	end if;


	exception
		when others then
		ds_resultado_w	:= '';
	end;
end if;

end;

RETURN ds_resultado_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
