SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_checkup_etapa ( nr_sequencia_p bigint, ie_opcao_p text default 'A') RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);
nr_sequencia_w		bigint;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	max(nr_seq_etapa)
	into STRICT	nr_sequencia_w
	from	checkup_etapa
	where	nr_sequencia	= nr_sequencia_p;


	if (ie_opcao_p	= 'A') then

		select	coalesce(ds_etapa_abrev,ds_etapa)
		into STRICT	ds_retorno_w
		from	etapa_checkup
		where	nr_sequencia	= nr_sequencia_w;

	elsif (ie_opcao_p	= 'C') then
		select	ds_etapa
		into STRICT	ds_retorno_w
		from	etapa_checkup
		where	nr_sequencia	= nr_sequencia_w;
	end if;
end if;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
