CREATE OR REPLACE FUNCTION obter_desc_ciap ( cd_ciap_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (cd_ciap_p IS NOT NULL AND cd_ciap_p::text <> '') then
	select	substr(ds_ciap,1,255)
	into STRICT	ds_retorno_w
	from	problema_ciap
	where	cd_ciap = cd_ciap_p
	and	ie_situacao = 'A';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
