CREATE OR REPLACE FUNCTION obter_desc_cid_autor (nr_sequencia_autor_p bigint) RETURNS varchar AS $body$
DECLARE


ds_cid_w	varchar(255);
ds_cids_w	varchar(4000);


C01 CURSOR FOR
	SELECT  	trim(both replace(replace(substr(obter_desc_cid(b.cd_doenca),1,255),'.',''),b.cd_doenca,''))
	from	AUTOR_DIAG_MEDICO a,
		autor_diag_doenca b
	where	a.nr_sequencia_autor = nr_sequencia_autor_p
	and	a.nr_sequencia	= b.nr_seq_autor_diag;



BEGIN

open C01;
loop
fetch C01 into
	ds_cid_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ds_cids_w := ds_cids_w || ds_cid_w|| chr(13);
end loop;
close C01;

return	ds_cids_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
