CREATE OR REPLACE FUNCTION obter_desc_cid_oft_auxilio (nr_seq_oft_auxilio_p bigint) RETURNS varchar AS $body$
DECLARE


c01 CURSOR FOR
	SELECT	obter_desc_cid_doenca(cd_cid_doenca) ds_cid
	from	oft_auxilio_visual_diag
	where	nr_seq_oft_auxilio = nr_seq_oft_auxilio_p;

vet01		c01%rowtype;
ds_cid_w	varchar(2000);


BEGIN

open c01;
loop
fetch c01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
		if (ds_cid_w IS NOT NULL AND ds_cid_w::text <> '') then
			ds_cid_w := substr(ds_cid_w ||', ', 0, 2000);
		end if;

		ds_cid_w := substr(ds_cid_w || vet01.ds_cid, 0, 2000);
	end;
end loop;
close c01;

return ds_cid_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
