CREATE OR REPLACE FUNCTION obter_desc_classificacao (nr_seq_classificacao_p man_classificacao.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_classificacao_w				dic_expressao.ds_expressao_br%type;


BEGIN

select	obter_desc_expressao(a.cd_exp_classificacao)
into STRICT	ds_classificacao_w
from	man_classificacao a
where	nr_sequencia = nr_seq_classificacao_p;

if (ds_classificacao_w = '' or coalesce(ds_classificacao_w::text, '') = '') then

	select	ds_classificacao
	into STRICT	ds_classificacao_w
	from	man_classificacao a
	where	nr_sequencia = nr_seq_classificacao_p;

end if;

return	ds_classificacao_w;

end;
$body$
