CREATE OR REPLACE FUNCTION obter_desc_classif_agenda_cir (nr_seq_classif_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(40);


BEGIN

if (nr_seq_classif_p IS NOT NULL AND nr_seq_classif_p::text <> '') THEN
	select	substr(ds_classificacao,1,40)
 	into STRICT	ds_retorno_w
	from	classif_agenda_cirurgica
	where	nr_sequencia = nr_seq_classif_p;
end if;

RETURN	ds_retorno_w;

END	;
$body$
