CREATE OR REPLACE FUNCTION obter_desc_classif_comp_dieta (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(90);
nr_seq_compo_w  bigint;


BEGIN

select	substr(max(b.ds_classificacao),1,90)
into STRICT	ds_retorno_w
from	composicao_dieta a,
	classif_composicao_dieta b
where	a.nr_sequencia	= nr_sequencia_p
and     b.nr_sequencia	= a.nr_seq_classif;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
