SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_classif_diag ( nr_atendimento_p bigint, nr_seq_episodio_p bigint, nr_seq_interno_p bigint) RETURNS varchar AS $body$
DECLARE


ds_classif_concatenado_w	varchar(10000) := '';
ds_classificacao_w			classificacao_diagnostico.ds_classificacao%type;

c01 CURSOR FOR
	SELECT	substr(c.ds_curta,1,255) ds_classificacao
	from	classificacao_diagnostico c,
			medic_diagnostico_doenca d,
			diagnostico_doenca e
	where	c.nr_sequencia	= d.nr_seq_classificacao
	and		d.nr_atendimento in (SELECT x.nr_atendimento from atendimento_paciente x, episodio_paciente y where x.nr_seq_episodio = y.nr_sequencia and y.nr_sequencia = nr_seq_episodio_p)
	and		e.nr_seq_interno = nr_seq_interno_p
	and		e.nr_atendimento = d.nr_atendimento
	and		e.cd_doenca = d.cd_doenca
	and		e.dt_diagnostico = d.dt_diagnostico
	and		(e.dt_liberacao IS NOT NULL AND e.dt_liberacao::text <> '')
	and		coalesce(e.dt_inativacao::text, '') = ''
	and		d.ie_situacao = 'A'
	and		c.ie_situacao = 'A';

c02 CURSOR FOR
	SELECT	substr(c.ds_curta,1,255) ds_classificacao
	from	classificacao_diagnostico c,
			medic_diagnostico_doenca d,
			diagnostico_doenca e
	where	c.nr_sequencia	= d.nr_seq_classificacao
	and		d.nr_atendimento = nr_atendimento_p
	and		e.nr_seq_interno = nr_seq_interno_p
	and		e.nr_atendimento = d.nr_atendimento
	and		e.cd_doenca = d.cd_doenca
	and		e.dt_diagnostico = d.dt_diagnostico
	and		(e.dt_liberacao IS NOT NULL AND e.dt_liberacao::text <> '')
	and		coalesce(e.dt_inativacao::text, '') = ''
	and		d.ie_situacao = 'A'
	and		c.ie_situacao = 'A';


BEGIN

if (nr_seq_episodio_p IS NOT NULL AND nr_seq_episodio_p::text <> '') then
	open c01;
	loop
	fetch c01 into
		ds_classificacao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		ds_classif_concatenado_w := ds_classif_concatenado_w || ds_classificacao_w || ' - ';
		end;
	end loop;
	close c01;
elsif (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	open c02;
	loop
	fetch c02 into
		ds_classificacao_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin
		ds_classif_concatenado_w := ds_classif_concatenado_w || ' - ' || ds_classificacao_w || ' - ';
		end;
	end loop;
	close c02;
else
	return ds_classif_concatenado_w;
end if;

return	ds_classif_concatenado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
