CREATE OR REPLACE FUNCTION obter_desc_classif_maior_prior ( nr_cirurgia_p bigint, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE



cd_tipo_cirurgia_w	bigint;
nr_seq_prior_cirurgia_w	bigint;
nr_seq_prior_atual_w	bigint;
ds_retorno_w		varchar(255);

c01 CURSOR FOR
	SELECT	cd_tipo_cirurgia
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_p
	
union

	SELECT	cd_tipo_cirurgia
	from	cirurgia
	where	nr_cirurgia = nr_cirurgia_p;


BEGIN

if (coalesce(nr_cirurgia_p,0) > 0) then

	open c01;
	loop
	fetch c01 into
		cd_tipo_cirurgia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	nr_seq_prior_atual_w := coalesce(obter_prior_cih_classif(cd_tipo_cirurgia_w),-1);
	if (coalesce(nr_seq_prior_atual_w,-1) > coalesce(nr_seq_prior_cirurgia_w,-1)) then
		nr_seq_prior_cirurgia_w := nr_seq_prior_atual_w;
		ds_retorno_w := substr(obter_desc_tipo_cirurgia(cd_tipo_cirurgia_w),1,255);
	end if;
	end loop;
	close c01;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
