CREATE OR REPLACE FUNCTION obter_desc_classif_pac (nr_seq_tipo_pac_p bigint) RETURNS varchar AS $body$
DECLARE


ds_classificacao_w	varchar(2000);

BEGIN

IF (nr_seq_tipo_pac_p IS NOT NULL AND nr_seq_tipo_pac_p::text <> '') THEN
	SELECT  ds_classif_paciente
	INTO STRICT	ds_classificacao_w
	FROM 	tipo_classificao_paciente
	WHERE 	ie_situacao = 'A'
	AND	nr_sequencia = nr_seq_tipo_pac_p;
END IF;

RETURN	ds_classificacao_w;

END;
$body$
LANGUAGE PLPGSQL
