CREATE OR REPLACE FUNCTION obter_desc_classif_tit_pag (nr_sequencia_p classe_titulo_pagar.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_classe_w		classe_titulo_pagar.ds_classe%type;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	ds_classe
	into STRICT	ds_classe_w
	from	classe_titulo_pagar
