CREATE OR REPLACE FUNCTION obter_desc_classif_unid ( cd_setor_unidade_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text) RETURNS varchar AS $body$
DECLARE


ds_classificacao_w	varchar(255);
nr_seq_classif_w	bigint;


BEGIN

select	max(nr_seq_classif)
into STRICT	nr_seq_classif_w
from	unidade_atendimento
where	cd_setor_atendimento = cd_setor_unidade_p
and	cd_unidade_basica = cd_unidade_basica_p
and 	cd_unidade_compl = cd_unidade_compl_p;


if (coalesce(nr_seq_classif_w,0) > 0) then

	select	substr(ds_classificacao,1,255)
	into STRICT	ds_classificacao_w
	from 	classif_unidade_atend
	where	nr_sequencia = nr_seq_classif_w;

end if;

return	ds_classificacao_w;

end;
$body$
LANGUAGE PLPGSQL
